<div class="col-sm-12  col-md-12 main">    <!--  filter row started  -->    <div class="row ">        <div class="box-shadow">            <div class="table-div">                <div class="table-head"> Filters </div>                <div class="row margin-top-xs" >                    <div class="btn-group margin-right-3 margin-left-md">                        <div class="form-group">                            <label for="search_Start_date">Start Date </label>                            <input id="search_Start_date" type="text" name="search_Start_date" class="datepicker-linked form-control" value="<?php  if(isset($_GET['start_date'])) { echo$_GET['start_date']; }else{ echo date('Y-m-01'); } ?>"  />                        </div>                    </div>                    <div class="btn-group margin-right-3 ">                        <div class="form-group">                            <label for="search_end_date">End Date </label>                            <input id="search_end_date" type="text" name="search_end_date" class="datepicker-linked form-control" value="<?php if(isset($_GET['end_date'])) { echo$_GET['end_date']; }else{ echo date('Y-m-31'); } ?>"  />                            <input type="hidden" id="apply_filer" name="apply_filer"  value="<?php if(isset($_GET['end_date'])) { echo "1"; }else{ echo "0"; } ?>" >                        </div>                    </div>                    <div class="btn-group margin-right-3 ">                        <div class="form-group no-border" style=" border: none; padding-top:10px;">                            <button type="button" class="btn btn-primary"  id="apply_filter"> Apply Filter</button>                            <button type="button" class="btn btn-danger"  id="remove_filter"> Remove Filter</button>                        </div>                    </div>                </div>            </div>            <!-- ended row -->        </div>    </div>    <!-- ended main filter row  -->		<div class="row">		<div class="box-shadow">		<div class="table-head"> All Trashed RFQ</div>		<div class="table-div padding-top-xs">		<table class="table RFQ_Trash_Tbl" id="RFQ_Trash_Tbl"  cellspacing="0" cellpadding="0">		<thead>        <th class="hidden-xs">RFQ ID</th>        <th class="hidden-xs" >RFQ Number</th>        <th class="hidden-xs" >RFQ Reason</th>        <th class="hidden-xs">Created Date</th>        <th class="hidden-xs">SOP</th>        <th class="hidden-xs">Requester</th>        <th class="hidden-xs">Status</th>        <th class="hidden-xs">Assigned To </th>        <th><?=$this->lang->line('application_action');?></th>        </thead>	 	</table>	 	<br clear="all">			</div></div><script type="text/javascript">	$(document).ready(function() {    // Setup - add a text input to each footer cell    $('.RFQ_Trash_Tbl thead tr').clone(true).appendTo( '.RFQ_Trash_Tbl thead' );    $('.RFQ_Trash_Tbl thead tr:eq(1) th').each( function (i) {        var title = $(this).text();        if(title!="Action" )        {            $(this).html('<input type="text" class="	" placeholder="Search ' + title + '" />');        }else{            $(this).html('');        }        $( 'input', this ).on( 'keyup change', function () {            if ( table.column(i).search() !== this.value ) {	                table	                    .column(i)	                    .search( this.value )	                    .draw();	            }	        } );    } );    var start_date=$("#search_Start_date").val();    var end_date=$("#search_end_date").val();    var applyfilter=$("#apply_filer").val();    //serverSide    var table =   $('#RFQ_Trash_Tbl').DataTable({        "bprocessing": true,        "bServerSide": true,        "orderCellsTop": true,        "fixedHeader": true,        "order": [[ 0, "desc" ]],        "lengthMenu": [[10, 25, 50, 100,-1], [10, 25, 50,100, "All"]],        "pageLength": defaulnumberofpages,        "ajax": {            url:"<?php echo base_url() . 'trash/getTrashed_RFQ_Archive'; ?>",            type : 'GET',            data: {start_date: start_date , end_date:end_date ,applyfilter:applyfilter},        },    });    // query string filter for filterin data in particular date    //search_Start_date, search_end_date    $("#apply_filter").click(function(){        var start_date=$("#search_Start_date").val();        var end_date=$("#search_end_date").val();        //alert(end_date+" >> "+ start_date)        var s_date = new Date(start_date);        var e_date = new Date(end_date);        if(e_date < s_date)        {            end_date=start_date;        }        $("#apply_filer").val(1);        applyfilter=1;        location.href = "?start_date=" + start_date +"&end_date="+end_date;    });    $("#remove_filter").click(function(){        $("#apply_filer").val(0);        applyfilter=1;        location.href =  location.protocol + '//' + location.host + location.pathname    });    //ended filter work} )</script><style type="text/css">	thead input {        width: 100%;    }</style>